// src/pages/Configuracion.js
import React from 'react';
import Layout from '../components/Layout';
import './Configuracion.css';
import { useUser } from '../context/UserContext';
import { useNavigate } from 'react-router-dom';

function Configuracion() {
  const { user, logout } = useUser();
  const navigate = useNavigate();

  const handleLogout = () => {
    logout();
    navigate('/'); // redirige al login
  };

  return (
    <Layout>
      <div className="config-container">
        <h2 className="config-title">Configuración de cuenta</h2>
        <p className="config-subtitle">Gestionar datos de tu cuenta</p>

        <div className="info-card">
          <h3>Información de la cuenta</h3>
          <div className="form-grid">
            <div>
              <label>Usuario</label>
              <div className="editable">
                <input type="text" value={user?.name || ''} readOnly />
                <button className="edit-btn">✏️</button>
              </div>
            </div>
            <div>
              <label>Correo electrónico</label>
              <div className="editable">
                <input type="text" value={user?.email || ''} readOnly />
                <button className="edit-btn">✏️</button>
              </div>
            </div>
            <div className="full-width">
              <label>Contraseña</label>
              <div className="editable">
                <input type="password" value="**********" readOnly />
                <button className="edit-btn">✏️</button>
              </div>
            </div>
          </div>

          <button className="save-btn">Guardar cambios</button>
        </div>

        <div className="logout-card">
          <img src="/logout.png" alt="Cerrar sesión" />
          <h4>Cerrar sesión</h4>
          <button className="logout-btn" onClick={handleLogout}>
            Cerrar sesión
          </button>
        </div>
      </div>
    </Layout>
  );
}

export default Configuracion;
