// src/pages/SincronizacionGoogle.js
import React, { useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import Layout from '../components/Layout';
import '../styles/SincronizacionGoogle.css';
import { generarPDFHorarios } from '../utils/pdfGenerator';

function SincronizacionGoogle() {
  const navigate = useNavigate();
  const fecha = new Date().toLocaleDateString();
  const hora = new Date().toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });

  const handleVolver = () => {
    navigate('/dashboard');
  };

  const handleGenerarPDF = () => {
    const horarios = JSON.parse(localStorage.getItem('horariosCSV')) || [];
    if (horarios.length > 0) {
      generarPDFHorarios(horarios);
    } else {
      alert('No hay horarios disponibles para exportar.');
    }
  };

  useEffect(() => {
    const horarios = JSON.parse(localStorage.getItem('horariosCSV')) || [];
    if (horarios.length > 0) {
      generarPDFHorarios(horarios); // Esto genera el PDF autom√°ticamente
    }
  }, []);

  return (
    <Layout>
      <div className="sync-container">
        <h2>Sincronizaci√≥n de Google Calendar</h2>

        <div className="sync-status">
          <div className="estado-box">
            <p>√öltima sincronizaci√≥n:</p>
            <h3>{fecha} a las {hora} ‚úÖ</h3>
          </div>
        </div>

        <div className="sync-analysis">
          <div className="card success">
            <img src="/success-icon.png" alt="check" />
            <p>Se complet√≥ la sincronizaci√≥n<br />de manera exitosa</p>
          </div>
          <div className="card error">
            <img src="/bug-icon.png" alt="bug" />
            <p>No hubo ning√∫n error en<br />la sincronizaci√≥n</p>
          </div>
        </div>

        {/* üîΩ Bot√≥n para exportar manualmente el PDF */}
        <button className="back-button" onClick={handleGenerarPDF} style={{ marginBottom: '10px' }}>
          Exportar horarios a PDF
        </button>

        <button className="back-button" onClick={handleVolver}>
          Volver al inicio
        </button>
      </div>
    </Layout>
  );
}

export default SincronizacionGoogle;
