import React from 'react';
import '../styles/Modal.css'; // usa el mismo estilo que SuccessModal

function ErrorModal({ titulo, mensaje, onClose }) {
  return (
    <div className="modal-overlay">
      <div className="modal">
        <h2 style={{ color: '#dc3545' }}>{titulo}</h2>
        <p>{mensaje}</p>
        <button onClick={onClose}>Cerrar</button>
      </div>
    </div>
  );
}

export default ErrorModal;
