// src/pages/Configuracion.js
import React from 'react';
import Sidebar from '../components/Sidebar';
import { useNavigate } from 'react-router-dom';
import { useUser } from '../context/UserContext';
import '../styles/Configuracion.css';

const Configuracion = () => {
  const { user, setUser } = useUser(); // Usa el contexto para acceder a setUser
  const navigate = useNavigate();

  const handleLogout = () => {
    setUser(null); // Restablece el usuario a null
    navigate('/'); // Redirige al login
  };

  return (
    <div className="configuracion-container">
      <Sidebar />
      <div className="configuracion-content">
        <img src={user?.picture || "../assets/user.png"} alt="User" className="user-image" />
        <h2>{user?.name || 'Usuario'}</h2>
        <p>{user?.email || 'correo@dominio.com'}</p>
        <button className="logout-button" onClick={handleLogout}>
          Cerrar Sesi√≥n
        </button>
      </div>
    </div>
  );
};

export default Configuracion;
