// src/pages/Login.js
import React from 'react';
import { GoogleLogin } from '@react-oauth/google';
import { useNavigate } from 'react-router-dom';
import { jwtDecode } from 'jwt-decode'; // Importa jwtDecode como un import nombrado

function Login({ setUser }) {
  const navigate = useNavigate();

  const handleSuccess = (response) => {
    const userObject = jwtDecode(response.credential); // Decodificar la informaci贸n de usuario
    setUser(userObject); // Almacenar el usuario en el estado global
    navigate('/dashboard'); // Redirigir al dashboard
  };

  const handleFailure = (error) => {
    console.log('Error:', error);
  };

  return (
    <div className="login">
      <h2>Iniciar Sesi贸n</h2>
      <GoogleLogin
        onSuccess={handleSuccess}
        onFailure={handleFailure}
        buttonText="Iniciar sesi贸n con Google"
      />
    </div>
  );
}

export default Login;
