import React from 'react';
import { GoogleLogin, useGoogleLogin } from '@react-oauth/google';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faGoogle } from '@fortawesome/free-brands-svg-icons';

function Login() {
  const googleLogin = useGoogleLogin({
    onSuccess: (response) => {
      console.log('Success:', response);
      // Aquí puedes manejar el token de Google para autenticar al usuario en tu backend
    },
    onError: (error) => {
      console.log('Error:', error);
    },
  });

  return (
    <div className="container d-flex justify-content-center align-items-center vh-100">
      <div className="card p-4" style={{ width: '400px' }}>
        <h2 className="text-center mb-4">Iniciar Sesión</h2>
        <form>
          <div className="mb-3">
            <label htmlFor="email" className="form-label">Correo electrónico</label>
            <input type="email" id="email" className="form-control" placeholder="Correo electrónico" />
          </div>
          <div className="mb-3">
            <label htmlFor="password" className="form-label">Contraseña</label>
            <input type="password" id="password" className="form-control" placeholder="Contraseña" />
          </div>
          <button type="submit" className="btn btn-primary w-100 mb-3">Iniciar Sesión</button>
          <button
            type="button"
            className="btn btn-outline-secondary w-100 mb-3 d-flex align-items-center justify-content-center"
            onClick={() => googleLogin()}
          >
            <FontAwesomeIcon icon={faGoogle} className="me-2" />
            Iniciar sesión con Google
          </button>
        </form>
        <p className="text-center">
          ¿No tienes cuenta? <a href="/register">Regístrate aquí</a>
        </p>
      </div>
    </div>
  );
}

export default Login;
