// src/utils/exportarPdf.js
import jsPDF from 'jspdf';
import autoTable from 'jspdf-autotable';

export function exportarHorariosAPDF(eventos) {
  const doc = new jsPDF();

  doc.setFontSize(14);
  doc.text('Horarios Sincronizados', 14, 20);

  const rows = eventos.map(evento => [
    evento.Clase || 'Sin clase',
    evento.Profesor || 'Sin profesor',
    evento.Dia || 'Sin día',
    evento.Hora || 'Sin hora',
    evento.Aula || 'Sin aula'
  ]);

  autoTable(doc, {
    head: [['Clase', 'Profesor', 'Día', 'Hora', 'Aula']],
    body: rows,
    startY: 30,
  });

  doc.save('horarios_sincronizados.pdf');
}
