// src/pages/Dashboard.js
import React, { useEffect } from 'react';
import './Dashboard.css';
import { useUser } from '../context/UserContext';

function Dashboard() {
  const { user, setUser } = useUser(); // ⬅ usamos el contexto global

  useEffect(() => {
    const token = localStorage.getItem('google_token');
    if (!token || user) return;

    fetch('https://www.googleapis.com/oauth2/v3/userinfo', {
      headers: {
        Authorization: `Bearer ${token}`,
      },
    })
      .then(res => {
        if (!res.ok) throw new Error('Token inválido o expirado');
        return res.json();
      })
      .then(data => {
        setUser({
          name: data.name,
          email: data.email,
          picture: data.picture,
        });
      })
      .catch(err => {
        console.error('❌ Error al obtener datos del usuario:', err);
      });
  }, [setUser, user]);

  return (
    <div className="dashboard">
      <aside className="sidebar">
        <div className="profile">
          <div className="avatar">
            {user?.picture ? (
              <img
                src={user.picture}
                alt="Foto de perfil"
                style={{ width: '100%', height: '100%', borderRadius: '50%' }}
              />
            ) : (
              <div style={{ width: '100%', height: '100%', background: '#fff', borderRadius: '50%' }} />
            )}
          </div>
          <h3>{user?.name || 'Usuario'}</h3>
          <p>{user?.email || 'correo@correo.com'}</p>
        </div>

        <nav className="menu">
          <button>Inicio</button>
          <button>Horarios</button>
          <button>Configuración</button>
        </nav>
      </aside>

      <main className="main-panel">
        {/* Aquí el contenido del panel, como calendario o eventos */}
      </main>
    </div>
  );
}

export default Dashboard;
