import jsPDF from 'jspdf';
import autoTable from 'jspdf-autotable';

export const generarPDFHorarios = (horarios) => {
  const doc = new jsPDF();

  doc.setFontSize(18);
  doc.text('Reporte de Horarios Importados', 14, 22);

  const fechaActual = new Date().toLocaleString('es-PE');
  doc.setFontSize(11);
  doc.text(`Generado por TEConTime - ${fechaActual}`, 14, 30);

  const headers = [['Clase', 'Profesor', 'Día', 'Hora', 'Aula']];
  const data = horarios.map(item => [
    item.Clase,
    item.Profesor,
    item.Día,
    item.Hora,
    item.Aula
  ]);

  autoTable(doc, {
    startY: 40,
    head: headers,
    body: data,
  });

  doc.save('horarios_importados.pdf');
};
