// src/pages/Login.js
import React from 'react';
import { GoogleLogin } from '@react-oauth/google';
import { useNavigate } from 'react-router-dom';
import jwtDecode from 'jwt-decode'; // Asegúrate de importar jwtDecode correctamente
import { useUser } from '../UserContext'; // Importa el contexto de usuario
import '../styles/Login.css';

function Login() {
  const { setUser } = useUser(); // Usa el contexto para obtener setUser
  const navigate = useNavigate();

  const handleSuccess = (response) => {
    const userObject = jwtDecode(response.credential); // Decodifica el token
    setUser({
      name: userObject.name,
      email: userObject.email,
      picture: userObject.picture,
    });
    navigate('/welcome');
  };

  const handleFailure = (error) => {
    console.log('Error:', error);
  };

  return (
    <div className="login">
      <div className="login-container">
        <h2>Iniciar Sesión</h2>
        <GoogleLogin
          onSuccess={handleSuccess}
          onFailure={handleFailure}
          buttonText="Iniciar sesión con Google"
        />
      </div>
    </div>
  );
}

export default Login;
