import jsPDF from 'jspdf';
import autoTable from 'jspdf-autotable';

export function exportarHorariosAPDF(eventos) {
  const doc = new jsPDF();

  // TÃ­tulo
  doc.setFontSize(18);
  doc.setTextColor(33, 37, 41); // Gris oscuro
  doc.text('ðŸ“… Horarios Sincronizados', 14, 20);

  // Fecha de exportaciÃ³n
  const fecha = new Date().toLocaleDateString('es-PE');
  const hora = new Date().toLocaleTimeString('es-PE', { hour: '2-digit', minute: '2-digit' });
  doc.setFontSize(10);
  doc.setTextColor(100);
  doc.text(`Exportado el ${fecha} a las ${hora}`, 14, 27);

  // Firma
  const firma = 'Exportado por Ayrton Leao Florian AstuhuamÃ¡n';
  
  // Datos
  const rows = eventos.map(evento => [
    evento.Clase || 'N/A',
    evento.Profesor || 'N/A',
    evento.DÃ­a || 'N/A',
    evento.Hora || 'N/A',
    evento.Aula || 'N/A',
  ]);

  // Tabla
  autoTable(doc, {
    head: [['Clase', 'Profesor', 'DÃ­a', 'Hora', 'Aula']],
    body: rows,
    startY: 35,
    styles: {
      fontSize: 11,
      cellPadding: 4,
    },
    headStyles: {
      fillColor: [41, 128, 185], // azul fuerte
      textColor: 255,
      halign: 'center',
    },
  });

  // Firma final
  const finalY = doc.lastAutoTable.finalY + 10;
  doc.setTextColor(70);
  doc.setFontSize(10);
  doc.text(firma, 14, finalY);

  doc.save('horarios_sincronizados.pdf');
}
