// src/pages/Login.js
import React from 'react';
import { GoogleLogin } from '@react-oauth/google';
import { useNavigate } from 'react-router-dom';
import { jwtDecode } from 'jwt-decode';
import { useUser } from '../context/UserContext';
import '../styles/Login.css';
import { FaUser, FaLock } from 'react-icons/fa';

function Login() {
  const { setUser } = useUser();
  const navigate = useNavigate();

  const handleSuccess = (response) => {
    const userObject = jwtDecode(response.credential);
    setUser({
      name: userObject.name,
      email: userObject.email,
      picture: userObject.picture,
      token: response.credential, // <-- importante
    });
    navigate('/welcome');
  };
  

  const handleFailure = (error) => {
    console.log('Error:', error);
  };

  return (
    <div className="login">
      <div className="login-box">
        <div className="avatar-placeholder"></div>

        <form className="form">
          <div className="input-group">
            <FaUser className="icon" />
            <input type="text" placeholder="USUARIO" disabled />
          </div>
          <div className="input-group">
            <FaLock className="icon" />
            <input type="password" placeholder="************" disabled />
          </div>
          <button className="login-btn" disabled>INICIAR SESIÓN</button>
        </form>

        <div className="google-section">
          <GoogleLogin
            onSuccess={handleSuccess}
            onFailure={handleFailure}
            text="signin_with"
          />
        </div>

        <div className="options">
          <label>
            <input type="checkbox" disabled /> Recordar contraseña
          </label>
          <a href="#" onClick={(e) => e.preventDefault()}>
            ¿Olvidó su contraseña? <span>Click aquí</span>
          </a>
        </div>
      </div>
    </div>
  );
}

export default Login;
